  postCode: string;
  appointmentType: string;
  status: string;
  profileImageUrl?: string;
  createdAt: Date;
  updatedAt: Date;
};

export type Form = {
  _id: string;
  residentId: string;
  formType: string;
  formData: Record<string, any>;
  supervisorId?: string;
  status: string;
  completedAt?:string;
  createdAt: string;
  updatedAt: string;
};

export type DisciplinaryAction = {
  _id: string;
  residentId: string;
  date: Date;
