// components/forms/FormGDetails.tsx
import React, { useEffect, useState, useRef } from "react";
import * as XLSX from "xlsx";

interface FormGDetailsProps {
  trainerId: string;
  onClose?: () => void;
}

interface PersonalInfo {
  Name: string;
  parentType: string;
  department: string;
  trainingYear: string;
  year: string;
}

interface Score {
  exam1Written: number;
  exam1Practical: number;
  exam2Written: number;
  exam2Practical: number;
  finalWritten: number;
  finalPractical: number;
  total: number;
  teacherName: string;
}

interface FormG {
  _id: string;
  trainer: string;
  personalInfo: PersonalInfo;
  scores: Score[];
  averageScore: number;
  departmentHead?: string;
  programHead?: string;
  hospitalHead?: string;
}

export default function FormGDetails({ trainerId, onClose }: FormGDetailsProps) {
  const [data, setData] = useState<FormG | null>(null);
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const printRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const res = await fetch(`/api/evaluationFormG?trainerId=${trainerId}`);
        if (res.status === 404) {
          setData(null);
          return;
        }
        if (!res.ok) throw new Error("Ø®Ø·Ø§ Ø¯Ø± Ø¯Ø±ÛŒØ§ÙØª Ø¯Ø§Ø¯Ù‡â€ŒÙ‡Ø§");
        const result = await res.json();
        const form = Array.isArray(result) ? result[0] : result;
        setData({
          ...form,
          scores: form.scores || [],
          personalInfo: form.personalInfo || {
            Name: "",
            parentType: "",
            department: "",
            trainingYear: "",
            year: "",
          },
        });
      } catch (err) {
        console.error("Error fetching Form G:", err);
        setData(null);
      } finally {
        setLoading(false);
      }
    };
