        if (!res.ok) throw new Error("ÙØ±Ù…ÛŒ Ø¨Ø±Ø§ÛŒ Ø§ÛŒÙ† ØªØ±ÛŒÙ†Ø± Ù…ÙˆØ¬ÙˆØ¯ Ù†ÛŒØ³Øª");
        const result = await res.json();
        if (Array.isArray(result) && result.length > 0) setData(result[0]);
