import express, { Request, Response } from "express";
import multer from "multer";
import path from "path";
import fs from "fs";
import { TrainerAction } from "../models/trainerAction";

const router = express.Router();

const uploadDir = path.join(process.cwd(), "uploads", "trainer-actions");
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

const storage = multer.diskStorage({
  destination: (req, file, cb) => {
    cb(null, uploadDir);
  },
  filename: (req, file, cb) => {
    const uniqueSuffix = Date.now() + "-" + Math.round(Math.random() * 1e9);
    cb(null, uniqueSuffix + "-" + file.originalname);
  },
});

const upload = multer({
  storage,
  limits: { fileSize: 10 * 1024 * 1024 },
});

router.post("/", upload.array("files", 10), async (req: Request, res: Response) => {
  try {
    const { trainerId, description } = req.body;
    const uploadedFiles = req.files as Express.Multer.File[];

    if (!trainerId || !description) {
      return res.status(400).json({ message: "Ø´Ù†Ø§Ø³Ù‡ ØªØ±ÛŒÙ†Ø± Ùˆ ØªÙˆØ¶ÛŒØ­Ø§Øª Ø§Ù„Ø²Ø§Ù…ÛŒ Ø§Ø³Øª" });
    }

    const files = uploadedFiles?.map((file) => ({
      filename: file.filename,
      originalName: file.originalname,
      path: file.path,
      size: file.size,
    })) || [];

    const newAction = new TrainerAction({
      trainer: trainerId,
      description,
      files,
    });

    const savedAction = await newAction.save();
    res.status(201).json(savedAction);
  } catch (error) {
    console.error("Error creating trainer action:", error);
    res.status(500).json({ message: "Ø®Ø·Ø§ Ø¯Ø± Ø«Ø¨Øª Ø§Ú©Ø´Ù†" });
  }
});

router.get("/download/:filename", (req: Request, res: Response) => {
  try {
    const { filename } = req.params;

    if (filename.includes('..') || filename.includes('/') || filename.includes('\\')) {
      return res.status(400).json({ message: "Ù†Ø§Ù… ÙØ§ÛŒÙ„ Ù†Ø§Ù…Ø¹ØªØ¨Ø± Ø§Ø³Øª" });
    }

    const filePath = path.join(uploadDir, filename);
    const resolvedPath = path.resolve(filePath);
    const resolvedUploadDir = path.resolve(uploadDir);

    if (!resolvedPath.startsWith(resolvedUploadDir)) {
      return res.status(403).json({ message: "Ø¯Ø³ØªØ±Ø³ÛŒ ØºÛŒØ±Ù…Ø¬Ø§Ø²" });
    }

    if (!fs.existsSync(resolvedPath)) {
      return res.status(404).json({ message: "ÙØ§ÛŒÙ„ ÛŒØ§ÙØª Ù†Ø´Ø¯" });
    }

    res.download(resolvedPath);
  } catch (error) {
    console.error("Error downloading file:", error);
    res.status(500).json({ message: "Ø®Ø·Ø§ Ø¯Ø± Ø¯Ø§Ù†Ù„ÙˆØ¯ ÙØ§ÛŒÙ„" });
  }
});

router.get("/:trainerId", async (req: Request, res: Response) => {
  try {
    const { trainerId } = req.params;

    const actions = await TrainerAction.find({ trainer: trainerId })
      .sort({ createdAt: -1 })
      .lean();

    res.status(200).json(actions);
  } catch (error) {
    console.error("Error fetching trainer actions:", error);
    res.status(500).json({ message: "Ø®Ø·Ø§ Ø¯Ø± Ø¯Ø±ÛŒØ§ÙØª Ø§Ú©Ø´Ù†â€ŒÙ‡Ø§" });
  }
});

router.delete("/:actionId", async (req: Request, res: Response) => {
  try {
    const { actionId } = req.params;

    const action = await TrainerAction.findById(actionId);

    if (!action) {
      return res.status(404).json({ message: "Ø§Ú©Ø´Ù† ÛŒØ§ÙØª Ù†Ø´Ø¯" });
    }

    if (action.files && action.files.length > 0) {
      action.files.forEach((file) => {
        if (fs.existsSync(file.path)) {
          fs.unlinkSync(file.path);
        }
      });
    }

    await TrainerAction.findByIdAndDelete(actionId);

    res.status(200).json({ message: "Ø§Ú©Ø´Ù† Ø­Ø°Ù Ø´Ø¯" });
  } catch (error) {
    console.error("Error deleting trainer action:", error);
    res.status(500).json({ message: "Ø®Ø·Ø§ Ø¯Ø± Ø­Ø°Ù Ø§Ú©Ø´Ù†" });
  }
});

export { router as trainerActionRoutes };

